import { FC } from "react";
import styled from "styled-components";

const StyledButton = styled.button`
  width: 8.8125rem;
  height: 2.5rem;
  flex-shrink: 0;
  background-color: #5DA552;
  border-top-left-radius: 20px;
  border-bottom-left-radius: 20px;
  border-top-right-radius: 20px;
  border-bottom-right-radius: 20px;
  color: #FFF;
  text-align: center;
  font-size: 0.75rem;
  font-style: normal;
  font-weight: 400;
  line-height: 1.125rem;
  border: none;
  cursor: pointer;
  outline: none;
  transition: background-color 0.3s, color 0.3s, border-color 0.3s; /* Transición suave */

  /* Estilos para hover */
  &:hover {
    background-color: #FFF; /* Color de fondo invertido */
    color: #5DA552; /* Color de texto invertido */
    border: 1px solid #5DA552; /* Agregar un borde del color del texto */
  }

  /* Estilos para active */
  &:active {
    background-color: #FFF; /* Color de fondo invertido */
    color: #5DA552; /* Color de texto invertido */
    border: 1px solid #5DA552; /* Agregar un borde del color del texto */
  }
`;

type ButtonProps = {
  text: string;
  onClick?: () => void;
};

const Button: FC<ButtonProps> = ({ text, onClick }) => {
  return <StyledButton onClick={onClick}>{text}</StyledButton>;
};

export default Button;
