import Modal from "./Modal";

export default {
  title: 'Atoms/Modal',
  component: Modal,
  tags: ["autodocs"],
  argTypes: {
    onClick: { action: 'clicked' },
  },
}

const handleClose = () => {
    return false
  };


export const ModalContainer = {
    args:{
        isOpen:false,
        onclose: handleClose()
    }
};
