import Modal from "./Modal";
import { useState } from "react";

export default {
  title: 'Atoms/Modal',
  component: Modal,
  tags: ["autodocs"],
}

const [isOpen, setIsOpen] = useState(false);

const handleClose = () => {
  setIsOpen(false);
};

export const ModalContainer = {
    args:{
        isOpen:{isOpen}, onClose:{handleClose}
    }
};
