import styled from "styled-components";

/**
 * Standard prototype that represents the Subtitle with a specific font size and line height and weight and text provided as properties.
 * @param text - the text content
 * @param color - (optional) text color content
 */

type TextProps = {
  text: string;
  color?: string;
};

const TextStyleComponent = styled.div<{ color?: string }>`
  color: ${(props) => (props.color ? props.color : "#646565")};
  font-size: 0.875rem;
  font-style: normal;
  font-weight: 400;
  line-height: 1.125rem;
`;

const TextContent: React.FC<TextProps> = ({ text, color }) => {
  return < TextStyleComponent color={color}>{text}</ TextStyleComponent>;
};

export default TextContent;
