import React, { useState } from "react";
import styled, { css } from "styled-components";

type FloatingLabelInputProps = {
  labelText: string;
  placeholderText: string;
};

type InputProps = {
  hasValue: boolean;
  isError: boolean;
  isFocused: boolean;
};

const FloatingLabelInputContainer = styled.div`
  position: relative;
  margin-bottom: 1rem;
`;

const Input = styled.input<InputProps>`
  width: 100%;
  height: 3.5rem;
  border: 1px solid transparent;
  border-radius: 0.25rem;
  background: #f0f0f0;
  color: rgba(0, 0, 0, 0.54);
  font-family: Roboto;
  font-size: 1rem;
  font-style: normal;
  font-weight: 400;
  line-height: 1rem;
  margin-top: 0.5rem;
  padding: 1.5rem 0.5rem 0.5rem 0.5rem;
  box-sizing: border-box;
  transition: border-color 0.3s ease-in-out;

  &:focus {
    outline: none;
    border-bottom-width: 2px;
    border-bottom-color: ${({ isError }) => (isError ? "red" : "#2979FF")};
  }

  ${({ hasValue, isFocused, isError }) =>
    (hasValue || isFocused) &&
    css`
      + label {
        transform: translateY(-130%) scale(0.75);
        color: ${isError ? "red" : "#2979FF"};
      }
    `}

  ${({ isError }) =>
    isError &&
    css`
      border-color: red;
    `}
`;

const Label = styled.label`
  position: absolute;
  top: 0.8rem;
  left: 0.5rem;
  color: #2979ff;
  font-family: Roboto;
  font-size: 0.75rem;
  font-style: normal;
  font-weight: 400;
  line-height: 0.875rem;
  transition: 0.3s ease-out;
  transform-origin: top left;
`;

const ErrorMessage = styled.span`
  color: red;
  font-family: Roboto;
  font-size: 0.7rem;
  font-style: normal;
  font-weight: 400;
  line-height: 0.875rem;
`;

const FloatingLabelInput: React.FC<FloatingLabelInputProps> = ({
  labelText,
  placeholderText,
}) => {
  const [value, setValue] = useState("");
  const [isFocused, setIsFocused] = useState(false);
  const [isError, setIsError] = useState(false);

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setValue(event.target.value);
    if (isError) {
      setIsError(false);
    }
  };

  const handleInputFocus = () => {
    setIsFocused(true);
  };

  const handleInputBlur = () => {
    setIsFocused(false);
  };

  const handleInputValidation = () => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (value.trim() === "") {
      setIsError(true);
    } else if (!emailRegex.test(value.trim())) {
      setIsError(true);
    } else {
      setIsError(false);
    }
  };

  return (
    <FloatingLabelInputContainer>
      <Input
        type="text"
        value={value}
        hasValue={!!value}
        isError={isError}
        placeholder={isFocused ? "" : placeholderText}
        isFocused={isFocused}
        onChange={handleInputChange}
        onFocus={handleInputFocus}
        onBlur={() => {
          handleInputBlur();
          handleInputValidation();
        }}
      />
      <Label>{labelText}</Label>
      {isError && (
        <ErrorMessage>Error: El campo no puede estar vac√≠o</ErrorMessage>
      )}
    </FloatingLabelInputContainer>
  );
};

export default FloatingLabelInput;
